            <p className="mt-2 text-sm text-gray-600">퀄리티 있는 작업을 위해 시간 제약 있는 작업은 진행이 어려운 점 양해 부탁드립니다.</p>
        </div>

        <div className="mt-8 grid grid-cols-1 gap-6 md:grid-cols-2">
          {SERVICES.map((service) => (
            <Card key={service.id} className="p-6">
              <div className="flex items-start justify-between gap-4">
                <div>
                  <h3 className="text-lg font-semibold whitespace-nowrap">{service.title}</h3>
                  {/*<div className="mt-1 text-sm text-gray-600">*/}
                  {/*  작업 시간 {service.workTime} · 인원 {service.workers}~{service.workers + 2}인*/}
                  {/*</div>*/}
                </div>
                <div className="flex flex-wrap gap-2">
                  {service.features.map((feature) => (
                    <Badge key={feature}>{feature}</Badge>
                  ))}
                </div>
              </div>

              <div className="mt-4">
                <h4 className="text-sm font-medium">작업 범위</h4>
                <ul className="mt-2 grid list-disc grid-cols-2 gap-x-6 gap-y-1 pl-5 text-sm">
                  {service.includedServices.map((item) => (
                    <li key={item}>{item}</li>
                  ))}
                </ul>

                {service.additionalInfo && (
                  <p className="mt-4 text-xs text-gray-600">{service.additionalInfo}</p>
                )}

                <p className="mt-1 text-xs text-gray-600">
                  에어컨 청소 가능(분해 청소 별도 금액 안내)
                </p>
              </div>
            </Card>
          ))}
        </div>
      </Container>
    </Section>
  );
}

'use client'
import { Container, NavLink, Button } from "../ui";
import { CONTACT_INFO, ANALYTICS_EVENTS } from "../constants";
import { track, scrollToSection } from "../utils";
import Image from "next/image";

export function Header() {
  const handleQuoteClick = () => {
    track(ANALYTICS_EVENTS.CTA_QUOTE_CLICK, { location: 'header' });
    window.open(CONTACT_INFO.formUrl, '_blank');
  };

  const handleNavClick = (sectionId: string) => {
    scrollToSection(sectionId);
  };

  return (
    <header className="sticky top-0 z-40 w-full border-b border-muted bg-[#0B256B] backdrop-blur-md shadow-sm">
      <Container>
        <div className="flex h-20 items-center justify-between gap-4">
          <a
            href="#home"
            className="flex h-full items-center gap-2 font-extrabold tracking-tight text-primary hover:text-primary-dark transition-colors"
            onClick={() => handleNavClick('home')}
          >
           <Image src="/logo.svg" alt="로고" className="h-full w-auto" />
          </a>

          <nav className="hidden h-full items-center gap-10 sm:flex">
            <NavLink href="#about" onClick={() => handleNavClick('about')}>
              회사 소개
            </NavLink>
            <NavLink href="#services" onClick={() => handleNavClick('services')}>
              서비스
            </NavLink>
            <NavLink href="#cases" onClick={() => handleNavClick('cases')}>
              시공 사례
            </NavLink>
            <NavLink href="#quote" onClick={() => handleNavClick('quote')}>
              견적 문의
            </NavLink>
            <NavLink href="#faq" onClick={() => handleNavClick('faq')}>
              문의·FAQ
            </NavLink>
          </nav>

          <Button onClick={handleQuoteClick} size="sm" className="btn-primary">
            견적 문의하기
          </Button>
        </div>
      </Container>
    </header>
  );
}

